import React from 'react';
import { SidebarLink, DropdownLink, SidebarLaber } from '../styles/Menu';


class MenuService extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      subNav: false
    }
  }
  showSubnav() {
    this.setState({ subNav: !this.state.subNav })
  }

  render() {
    const item = this.props.item;
    console.log(item)
    console.log(this.state.subNav)
    return (
      <>
        <SidebarLink to={item.path} onClick={() => this.showSubnav()}>
          <div>
            {item.icon}
            <SidebarLaber>{item.title}</SidebarLaber>
          </div>
          <div>
            {item.subNav && this.state.subNav
              ? item.iconOpened
              : item.subNav
                ? item.iconClosed
                : null}
          </div>
        </SidebarLink>
        {this.state.subNav &&
          item.subNav.map((item, index) => {
            return (
              <DropdownLink to={item.path} key={index}  >
                {item.icon}
                <SidebarLaber>{item.title}</SidebarLaber>
              </DropdownLink>
            )
          })
        }
      </>
    );
  }
}
export default MenuService;